mapper.select:
select count(*) from account
where 1=1
and name like '%${name}%'
and realName like '%${realName}%'
and phoneNum like '%${phoneNum}%' 
and idCard like '%${idCard}%'
and status=#{status}
limit #{pageSize}
offset #{offset}

