mapper.select:
SELECT count(*) FROM bookorder b
JOIN
(SELECT id FROM account WHERE phoneNum like '%${phoneNum}%')a
on b.accountId=a.id
WHERE 1=1
AND name like '%${name}%'
AND phoneNum like '%${phoneNum}%'
AND arriveDate >= #{arriveDate}
AND leaveDate <= #{leaveDate}
AND status = #{status}
limit #{pageSize}
offset #{offset}

SELECT * FROM bookorder b
JOIN
(SELECT id FROM account WHERE phoneNum like '%${accountPhone}%')a
on b.accountId=a.id
WHERE 1=1
AND name like '%${name}%'
AND phoneNum like '%${phoneNum}%'
AND arriveDate >= #{arriveDate}
AND leaveDate <= #{leaveDate}
AND status = #{status}
limit #{pageSize}
offset #{offset}
